import actions from "./actions";
import { DataService } from "../../config/dataservice/Dataservice";
import { API } from "../../config/api";
import { Alert } from "@mui/material";
import { useEffect } from "react";
import { useDispatch } from "react-redux";

const {getRepositoryData}=actions;
const getRepository = () => {
  const dispatch = useDispatch();

  useEffect(() => {
    const fetchRepositories = async () => {
      const perPage = 10; // Number of items per page
      let page = 1; // Start from the first page

      while (true) {
        try {
          const queryString = `?q=created:>2017-10-22&page=${page}`;
          const getData = await DataService.get(API.repos.get + queryString);

          if (!getData.data.error) {
            if (getData.data.items.length === 0) {
              // No more data, exit the loop
              break;
            }
            // Dispatch the fetched data to your Redux store
            dispatch(getRepositoryData(getData.data));

            // Move to the next page
            page++;
          } else {
            // Handle error, maybe dispatch an action to update the state with an error message
            // <Alert severity="error">{getData.data.message}</Alert>
            break;
          }
        } catch (error) {
          // Handle error, maybe dispatch an action to update the state with an error message
          // <Alert severity="error">{error.message}</Alert>
          break;
        }
      }
    };

    fetchRepositories();
  }, [dispatch]);

  return null; // Return null or any other JSX if needed
};

export default getRepository;
