import React from "react";
import { useEffect } from "react";
import { styled } from "@mui/material/styles";
import Box from "@mui/material/Box";
import Paper from "@mui/material/Paper";
import { useDispatch, useSelector } from "react-redux";
import { getRepository } from "../../redux/repos/actioncCreator";
import "./repos.css";
import { Avatar, Grid } from "@mui/material";
import moment from "moment";
import HighchartsReact from "highcharts-react-official";
import chart from "../chart/chart"
import Highcharts from "highcharts";
import KeyboardArrowRightOutlinedIcon from '@mui/icons-material/KeyboardArrowRightOutlined';
const Repos = () => {
  const dispatch = useDispatch();

  useEffect(() => {
    FetchRepoData();
  }, []);
  const chartOptions = {
    title: {
      text: "Sample Chart Title",
    },
    xAxis: {
      categories: ["Category 1", "Category 2", "Category 3"],
    },
    yAxis: {
      title: {
        text: "Value",
      },
    },
    series: [
      {
        name: "Series 1",
        data: [10, 20, 15],
      },
    ],
  };
  const Item = styled(Paper)(({ theme }) => ({
    backgroundColor: theme.palette.mode === "dark" ? "#1A2027" : "#fff",
    ...theme.typography.body2,
    padding: theme.spacing(4),
    margin:theme.spacing(6),
    textAlign: "center",
    color: theme.palette.text.secondary,
  }));
  const FetchRepoData = async () => {
    await dispatch(getRepository());
  };

  const RepoData = useSelector((state) => state.RepositoryReducer.repo.items);
  console.log("data", RepoData);
  return (
    <div className="container">
      <p className="Heading">Most Starred Repos</p>

      <div>
        <Grid container spacing={2}>
          {RepoData?.map((data) => {
            return (
              <>
                <Grid item xs={12} sm={12} md={12}>
                  <Item className="display-div">
                 <div className="avatar-div">
             <Avatar alt="avatar-img" src={data.owner.avatar_url} className="avatar-img"/>
                 </div>
                 <div className="content-div">
                 <div className="repo-name">
                  {data.full_name}
                 </div>
                 <div className="repo-description">
                  {data.description}
                 </div>
                 <div className="combine-count-div">
                  <div className="stars-count">
                   Stars:- {data.stargazers_count}
                  </div>
                  <div className="issues-count">
                    Issues:-{data.open_issues_count}
                  </div>
                  <div className="date-div">

                    Last Pushed {moment(data.pushed_at).format("MMMM D,YYYY")} by {data.owner.login}
                  </div>
                 </div>
                 </div>
                 <div>
                  <KeyboardArrowRightOutlinedIcon className="arrow"/>
                 </div>
                  </Item>
                </Grid>
              </>
            );
          })}
        </Grid>

      </div>
    
      <div className="chart-container">
        <HighchartsReact highcharts={Highcharts} options={chartOptions} />
      </div>
    
    </div>
  );
};

export default Repos;
